import React from 'react'
import PropTypes from 'prop-types'
// Calling an action or getting state, use connect
import { connect } from 'react-redux'

const Alert = ({ alerts }) => alerts !== null && alerts.length > 0 && alerts.map(alert => (
    <div key={alert.id} className={`st-card-el-h1 st-${alert.alertType}`}>
        { alert.msg }
    </div>
))

Alert.propTypes = {
    alerts: PropTypes.func.isRequired

}

// Mapping state to properties (Getting the state and mapping them to properties)
const mapStateToProps = (state) => ({
    alerts: state.alert
})

export default connect(mapStateToProps)(Alert)
