import axios from 'axios';
import { setAlert } from './alert';
import {
    S_REGISTER_SUCCESS, 
    S_REGISTER_FAILED,
    S_USER_LOADED,
    S_AUTH_ERROR,
    } from './types';
import setAuthToken from '../utils/setAuthToken';

// Load user
export const s_loadUser = () => async dispatch => {
    console.log(`here is the token: ${localStorage.token}`);
    // check for token
    if(localStorage.token) {
        setAuthToken(localStorage.token);
    }
    // make request, res.data returns the user
    try {
        const res = await axios.get('./api/s_auth')
        dispatch({
            type: S_USER_LOADED,
            payload: res.data
        })
    } catch (err) {
        dispatch({
            type: S_AUTH_ERROR
        })
    }
}

// Register user
export const register = ({name, email, password}) => async dispatch => {
    const config ={
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({name, email, password});
    try {
        const res = await axios.post('/api/s_users', body, config);
        dispatch({
            type: S_REGISTER_SUCCESS,
            payload: res.data
        });
    } catch (err) {
        const errors = err.response.data.errors;
        if(errors) {
            errors.forEach(error => dispatch(setAlert(error.msg, 'red')));
        }
        dispatch({
            type: S_REGISTER_FAILED
        })
    }
}
