import {
    GET_COMPANY_PROFILE, 
    GET_SOLVER_PROFILE, 
    C_PROFILE_ERROR, 
    S_PROFILE_ERROR} from '../actions/types';

const initialState = {
    profile: null,
    profiles: [],
    loading: null,
    error: {}
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch(type){
        case GET_COMPANY_PROFILE:
        return {
            ...state,
            profile: payload,
            loading: false
        };
        case C_PROFILE_ERROR:
        return {
            ...state,
            error: payload,
            loading: false
        };
        default:
        return state;

    }
}
