import React, { Fragment, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import setAuthToken from './utils/setAuthToken';
// redux
import { Provider } from 'react-redux';
import store from './store';
import { loadUser } from './actions/auth';
import { s_loadUser } from './actions/s_auth';
// COMPONENTS
// common
import Landing from './components/layout/Landing';
// company
import Company_login from './components/company/auth/Company_login';
// solver
import Solver_login from './components/solver/auth/Solver_login';
const jwtDecode = require('jwt-decode');

const accesscontrol = 10;

if(localStorage.token) {
  const decoded = jwtDecode(localStorage.token);
  accesscontrol = decoded.user.role;
}

const accesscontrolCheck = (accesscontrol) => {
  if(accesscontrol == 0) {
    return loadUser();
  } else if (accesscontrol == 1) {
    return s_loadUser();
  }
}


if(localStorage.token) {
  setAuthToken(localStorage.token);
}

const App = () => {

  useEffect(() => {
    store.dispatch(accesscontrolCheck)
  }, []);

  return (
  <Provider store={store}>
    <Router>
      <Fragment>

  <Switch>
    <Route exact path='/' component={Landing} />

    <Route exact path="/company_login" component={Company_login} />

    <Route exact path ="/solver_login" component={Solver_login} />
  </Switch>

      </Fragment>
    </Router>
  </Provider>
  )};


export default App;
